import React,{useEffect,useState} from 'react';
import {BrowserRouter as Router,Link} from 'react-router-dom';
const Home = () => {

    const [articles, setArticles] = useState([]);
    useEffect(() =>{
            getArticles();
        },[])

    const getArticles = async () => {
        const response = await fetch('http://127.0.0.1:8000/posts/home/')
        const data = await response.json();
        setArticles(data.articles)
    }
    return(
        <div>
            <Link to={'/myaccount'}>account</Link><br />
            <Link to={'/postarticle'}>post</Link>

            {articles.map(article => (
                <div>
                    key={article.id}
                    <Link to={`/profile/${article.author}`}>{article.author}</Link>
                    <image src={article.post_image} alt=""/>
                    <h3>{article.article_title}</h3>
                    <h5>{article.article_body}</h5>
                </div>
            ))}
        </div>
    )
}

export default Home
