import React,{ useState } from 'react'
import axios from 'axios'
import './register.css'
const Register = () => {

    const [username, setUsername] = useState("")
    const [email, setEmail] = useState("")
    const [password1, setPassword1] = useState("")
    const [password2, setPassword2] = useState("")
    const [token, setToken] = useState("");
    const UsernameHandler = e => {
        setUsername(e.target.value)
    }
    const emailHandler = e => {
        setEmail(e.target.value)
    }
    const password1Handler = e => {
        setPassword1(e.target.value)
    }
    const password2Handler = e => {
        setPassword2(e.target.value)
    }
    const submitHandler = e => {
        e.preventDefault()
        const data = {
            username:username,
            email:email,
            password1:password1,
            password2:password2
        }
        axios.post("http://127.0.0.1:8000/users/register/", data)
        .then(response => {
                console.log(response)
                setToken(response.data.token)
        })
        .catch(error => {
            console.log(error)
        })
    }

    localStorage.setItem('token',token)

    return (
        <div className="Container">
                <div className="Sign-up">
                    <form onSubmit={submitHandler}>
                    <div className="signup-username">
                        <input type="text" name="" value={username} onChange={UsernameHandler} placeholder="username" /><br/>
                    </div>
                    <div className="sinup-email">
                        <input type="email" name="email" value={email} onChange={emailHandler} placeholder="email"/><br/>
                    </div>
                    <input type="password" name="password1" value={password1} onChange={password1Handler} placeholder="password" /><br/>
                    <input type="password" name="password2" value={password2} onChange={password2Handler} placeholder="confirm password" /><br/>
                        <br/>
                        <input type="submit" value="submit" />
                    </form>
                </div> 
        </div>
    )


}
export default Register
