import React, { useState } from 'react'
import axios from 'axios'
const SignIn = () => {

    const [username, setUsername] = useState("")
    const [password, setPassword] = useState("")

    const UsernameHandler = e => {
        setUsername(e.target.value)
    }

    const passwordHandler = e => {
        setPassword(e.target.value)
    }
    const submitHandler = e => {
        e.preventDefault()
        const data = {
            username: username,
            password: password
        }
        axios.post("http://127.0.0.1:8000/users/login/", data)
            .then(response => {
                console.log(response)
            })
            .catch(error => {
                console.log(error)
            })
    }
    console.log(localStorage.getItem('token'))
    return (
        <div className="Container">
            <h1>freetalk</h1>
            <div className="Sign-in">
                <h2>sign in</h2>
                <form onSubmit={submitHandler}>
                    <input type="text" name="username" placeholder="username" value={username} onChange={UsernameHandler} />
                    <input type="password" name="password" value={password} onChange={passwordHandler} placeholder="password" />
                    <input type="submit" value="submit" placeholder="Sign in" />
                </form>
            </div>
        </div>
    )


}
export default SignIn
