import React from 'react';
import axios from 'axios';

const PostDelete = ({ match }) =>{
        axios.delete(`http://localhost:8000/articles/delete/${match.params.id}`)
            .then(response => {
                console.log(response)
            })
            .catch(error => {
                console.log(error)
            })

}
export default PostDelete
