import React,{useEffect,useState} from 'react';
import {BrowserRouter as Router,Link} from 'react-router-dom';
import './home.css'
const Home = () => {

    const [articles, setArticles] = useState([]);
    useEffect(() =>{
            getArticles();
        },[])

    const getArticles = async () => {
        const response = await fetch('http://127.0.0.1:8000/posts/home/')
        const data = await response.json();
        setArticles(data.articles)
    }
    return(
        <div className="Container">
            <div className="nav">
                <Link to={'/myaccount'}>Myaccount</Link>
                <Link to={'/postarticle'}>post</Link>
            </div>
            {articles.map(article => (
                <div className="post">
                    <div className="post_id">key={article.id}</div>
                    <Link className="author" to={`/profile/${article.author}`}>Author : {article.author}</Link>
                    <image src={article.post_image} alt=""/>
                    <h4>Title : {article.article_title}</h4>
                    <h6>{article.article_body}</h6>
                </div>
            ))}
        </div>
    )
}

export default Home
