import React,{useEffect,useState} from 'react';
import { Link } from 'react-router-dom';
import './posts.css'
import './home.css'
const Myaccount = () => {
    console.log(localStorage.getItem('token'))
    const [details, setDetails] = useState({});
    const [articles, setArticles] = useState([]);
    const [tok, setTok] = useState(localStorage.getItem('token'));
    useEffect(() =>{
        getAccount();
    },[])
    const getAccount = async () =>{
        const response = await fetch('http://127.0.0.1:8000/users/myaccount/',{
        headers:{
                'Content-Type':'application/json',
                Authorization:tok,
        }})
        const data = await response.json();
        setDetails(data);
        setArticles(data.article);
        console.log(tok)

    } 
    return(
        <div>
            <div className="nav">
                <h4>{details.username}</h4>
                <h4>{details.email}</h4>
            </div>
            {articles.map(article => (
                <div className="post">
                    <image src={article.post_image} alt="" />
                    <h4 className="title">{article.article_title}</h4>
                    <h6 className="content">{article.article_body}</h6>
                </div>
            ))}
        </div>
    )
}

export default Myaccount;
