import React,{useState,useEffect} from 'react'

const Profile = ({ match }) => {
    useEffect(() => {
        getProfile();
    },[])
    const [profile, setProfile] = useState({});
    console.log("hello")
    console.log(match)

    const getProfile = async () => {
        const response = await fetch(`http://127.0.0.1:8000/posts/profile/${match.params.id}`)
        const data = await response.json();
        setProfile(data)
        console.log(data)
    }
    return(
        <div>
            <h2>{profile.username}</h2>
            <h2>{profile.email}</h2>
            <div>
                {profile.map(article => (
                    <div>
                        <image src={article.post_image} alt="" />
                        <h4>{article.article_title}</h4>
                        <h6>{article.article_body}</h6>
                    </div>
                ))}
            </div>
        </div>
    )
}
export default Profile
