import React, { useState } from 'react';
import axios from 'axios';
const PostArticle = () => {
    const [title, setTitle] = useState('');
    const [content, setContect] = useState('');
    const [tok, setTok] = useState('');

    const titleHandler = e => {
        setTitle(e.target.value)
    }

    const contentHandler = e => {
        setContect(e.target.value)
    }


    setTok(localStorage.getItem('token'))
    const submitHandler = e => {
        e.preventDefault();
        const data = {
            title: title,
            content: content,
            token: tok
        }
        axios.post("http://127.0.0.1:8000/posts/articles/", data)
            .then(response => {
                console.log(response)
            })
            .catch(error => {
                console.log(error)
            })
    }
    return (
        <div>
            <form onSubmit={submitHandler}>
                <input type="text" placeholder="Title" value={title} onChange={titleHandler} /> <br />
                <textarea rows="25" cols="100" value={content} onChange={contentHandler}>
                </textarea><br />
                <input type="submit" value="post" />
            </form>
        </div>
    )
}

export default PostArticle
