import React,{useState} from 'react';
import axios from 'axios';
const Create = () => {
    const [title, setTitle] = useState('');
    const [content, setContect] = useState('');
    //const [image, setImage] = useState('');

    const titleHandler = e => {
        setTitle(e.target.value)
    }

    const contentHandler = e => {
        setContect(e.target.value)
    }

    const submitHandler = e => {
        e.preventDefault();
        const data = {
            title:title,
            content:content
        }
        axios.post("http://127.0.0.1:8000/users/login/", data)
            .then(response => {
                console.log(response)
            })
            .catch(error => {
                console.log(error)
            })
    }
    return(
        <div>
            <form onSubmit={submitHandler}>
                <input type="image" />
                <input type="text" placeholder="Title" value={title} onChange={titleHandler}/>
                <p>
                    <textarea rows="20" column="200" value={content} onChange={contentHandler}>
                        article content
                    </textarea>
                </p>
                <input type="submit">post</input>
            </form>
        </div>
    )
} 

export default Create;
